properties_schema:
[
  {
    "displayName": "Authentication",
    "name": "authentication",
    "type": "options",
    "default": "accessToken",
    "options": [
      {
        "name": "Access Token",
        "value": "accessToken"
      },
      {
        "name": "OAuth2",
        "value": "oAuth2"
      }
    ]
  },
  {
    "displayName": "Resource",
    "name": "resource",
    "type": "options",
    "default": "task",
    "options": [
      {
        "name": "Project",
        "value": "project"
      },
      {
        "name": "Subtask",
        "value": "subtask"
      },
      {
        "name": "Task",
        "value": "task"
      },
      {
        "name": "Task Comment",
        "value": "taskComment"
      },
      {
        "name": "Task Project",
        "value": "taskProject"
      },
      {
        "name": "Task Tag",
        "value": "taskTag"
      },
      {
        "name": "User",
        "value": "user"
      }
    ],
    "noDataExpression": true
  },
  {
    "displayName": "Operation",
    "name": "operation",
    "type": "options",
    "default": "create",
    "options": [
      {
        "name": "Create",
        "value": "create",
        "description": "Create a subtask",
        "action": "Create a subtask"
      },
      {
        "name": "Get Many",
        "value": "getAll",
        "description": "Get many subtasks",
        "action": "Get many subtasks"
      }
    ],
    "displayOptions": {
      "show": {
        "resource": [
          "subtask"
        ]
      }
    },
    "noDataExpression": true
  },
  {
    "displayName": "Parent Task ID",
    "name": "taskId",
    "type": "string",
    "default": "",
    "description": "The task to operate on",
    "required": true,
    "displayOptions": {
      "show": {
        "operation": [
          "create"
        ],
        "resource": [
          "subtask"
        ]
      }
    }
  },
  {
    "displayName": "Name",
    "name": "name",
    "type": "string",
    "default": "",
    "description": "The name of the subtask to create",
    "required": true,
    "displayOptions": {
      "show": {
        "operation": [
          "create"
        ],
        "resource": [
          "subtask"
        ]
      }
    }
  },
  {
    "displayName": "Additional Fields",
    "name": "otherProperties",
    "type": "collection",
    "default": {},
    "options": [
      {
        "displayName": "Assignee Name or ID",
        "name": "assignee",
        "type": "options",
        "typeOptions": {
          "loadOptionsMethod": "getUsers"
        },
        "default": "",
        "description": "Set Assignee on the subtask. Choose from the list, or specify an ID using an <a href=\"https://docs.n8n.io/code/expressions/\">expression</a>."
      },
      {
        "displayName": "Assignee Status",
        "name": "assignee_status",
        "type": "options",
        "options": [
          {
            "name": "Inbox",
            "value": "inbox"
          },
          {
            "name": "Today",
            "value": "today"
          },
          {
            "name": "Upcoming",
            "value": "upcoming"
          },
          {
            "name": "Later",
            "value": "later"
          }
        ],
        "default": "inbox",
        "description": "Set Assignee status on the subtask (requires Assignee)"
      },
      {
        "displayName": "Completed",
        "name": "completed",
        "type": "boolean",
        "default": false,
        "description": "Whether the subtask should be marked completed"
      },
      {
        "displayName": "Due On",
        "name": "due_on",
        "type": "dateTime",
        "default": "",
        "description": "Date on which the time is due"
      },
      {
        "displayName": "Liked",
        "name": "liked",
        "type": "boolean",
        "default": false,
        "description": "Whether the task is liked by the authorized user"
      },
      {
        "displayName": "Notes",
        "name": "notes",
        "type": "string",
        "typeOptions": {
          "rows": 5
        },
        "default": "",
        "description": "The task notes"
