properties_schema:
[
  {
    "displayName": "Action",
    "name": "action",
    "type": "options",
    "default": "record",
    "options": [
      {
        "name": "Create Record",
        "value": "create"
      },
      {
        "name": "Delete Record",
        "value": "delete"
      },
      {
        "name": "Duplicate Record",
        "value": "duplicate"
      },
      {
        "name": "Edit Record",
        "value": "edit"
      },
      {
        "name": "Find Records",
        "value": "find"
      },
      {
        "name": "Get Records",
        "value": "records"
      },
      {
        "name": "Get Records By ID",
        "value": "record"
      },
      {
        "name": "Perform Script",
        "value": "performscript"
      }
    ]
  },
  {
    "displayName": "Layout Name or ID",
    "name": "layout",
    "type": "options",
    "default": "",
    "description": "FileMaker Layout Name. Choose from the list, or specify an ID using an <a href=\"https://docs.n8n.io/code/expressions/\">expression</a>.",
    "options": [],
    "required": true,
    "displayOptions": {},
    "typeOptions": {
      "loadOptionsMethod": "getLayouts"
    }
  },
  {
    "displayName": "Record ID",
    "name": "recid",
    "type": "number",
    "default": "",
    "description": "Internal Record ID returned by get (recordid)",
    "required": true,
    "displayOptions": {
      "show": {
        "action": [
          "record",
          "edit",
          "delete",
          "duplicate"
        ]
      }
    }
  },
  {
    "displayName": "Offset",
    "name": "offset",
    "type": "number",
    "default": 1,
    "description": "The record number of the first record in the range of records",
