  },
  {
    "operation": "delete",
    "name": "Delete Rows or Columns",
    "description": "Delete columns or rows from a sheet"
  },
  {
    "operation": "read",
    "name": "Get Row(s)",
    "description": "Retrieve one or more rows from a sheet"
  },
  {
    "operation": "update",
    "name": "Update Row",
    "description": "Update an existing row in a sheet"
  }
]

