    "displayName": "This node must be connected to an AI agent. <a data-action='openSelectiveNodeCreator' data-action-parameter-creatorview='AI'>Insert one</a>",
    "name": "notice",
    "type": "notice",
    "default": "",
    "typeOptions": {
      "containerClass": "ndv-connection-hint-notice"
    }
  },
  {
    "displayName": "See an example of a workflow to suggest meeting slots using AI <a href=\"/templates/1953\" target=\"_blank\">here</a>.",
    "name": "noticeTemplateExample",
    "type": "notice",
    "default": ""
  },
  {
    "displayName": "Name",
    "name": "name",
    "type": "string",
    "default": "",
    "description": "The name of the function to be called, could contain letters, numbers, and underscores only",
    "displayOptions": {
      "show": {
        "@version": [
          {
            "_cnd": {
              "lte": 2.1
            }
          }
        ]
      }
    }
  },
  {
    "displayName": "Description",
    "name": "description",
    "type": "string",
    "default": "",
    "typeOptions": {
      "rows": 3
    }
  },
  {
    "displayName": "This tool will call the workflow you define below, and look in the last node for the response. The workflow needs to start with an Execute Workflow trigger",
    "name": "executeNotice",
    "type": "notice",
    "default": ""
  },
  {
    "displayName": "Source",
    "name": "source",
    "type": "options",
    "default": "database",
    "description": "Where to get the workflow to execute from",
    "options": [
      {
        "name": "Database",
        "value": "database",
        "description": "Load the workflow from the database by ID"
      },
      {
        "name": "Define Below",
        "value": "parameter",
        "description": "Pass the JSON code of a workflow"
      }
    ]
  },
  {
    "displayName": "Workflow",
    "name": "workflowId",
    "type": "workflowSelector",
    "default": "",
    "required": true,
    "displayOptions": {
      "show": {
        "source": [
          "database"
        ]
      }
    }
  },
  {
    "displayName": "Workflow Inputs",
    "name": "workflowInputs",
    "type": "resourceMapper",
    "default": {
      "mappingMode": "defineBelow",
      "value": null
    },
    "required": true,
    "displayOptions": {
      "show": {
        "source": [
          "database"
        ]
      },
      "hide": {
        "workflowId": [
          ""
        ]
      }
    },
    "typeOptions": {
      "loadOptionsDependsOn": [
        "workflowId.value"
      ],
      "resourceMapper": {
        "localResourceMapperMethod": "loadSubWorkflowInputs",
        "valuesLabel": "Workflow Inputs",
        "mode": "map",
        "fieldWords": {
          "singular": "workflow input",
          "plural": "workflow inputs"
        },
        "addAllFields": true,
        "multiKeyMatch": false,
        "supportAutoMap": false
      }
    },
    "noDataExpression": true
  },
  {
    "displayName": "Workflow JSON",
    "name": "workflowJson",
    "type": "json",
    "default": "\n\n\n\n\n\n\n\n\n",
    "description": "The workflow JSON code to execute",
    "required": true,
    "displayOptions": {
      "show": {
        "source": [
          "parameter"
        ]
      }
    },
    "typeOptions": {
      "rows": 10
    }
  }
]

[
