  {
    "displayName": "Listen For",
    "name": "triggerMode",
    "type": "options",
    "default": "createTrigger",
    "options": [
      {
        "name": "Table Row Change Events",
        "value": "createTrigger",
        "description": "Insert, update or delete"
      },
      {
        "name": "Advanced",
        "value": "listenTrigger",
        "description": "Listen to existing Postgres channel"
      }
    ]
  },
  {
    "displayName": "Schema Name",
    "name": "schema",
    "type": "resourceLocator",
    "default": {
      "mode": "list",
      "value": "public"
    },
    "required": true,
    "displayOptions": {
      "show": {
        "triggerMode": [
          "createTrigger"
        ]
      }
    }
  },
  {
    "displayName": "Table Name",
    "name": "tableName",
    "type": "resourceLocator",
    "default": {
      "mode": "list",
      "value": ""
    },
    "required": true,
    "displayOptions": {
      "show": {
        "triggerMode": [
          "createTrigger"
        ]
      }
    }
  },
  {
    "displayName": "Channel Name",
    "name": "channelName",
    "type": "string",
    "default": "",
    "description": "Name of the channel to listen to",
    "required": true,
    "displayOptions": {
      "show": {
        "triggerMode": [
          "listenTrigger"
        ]
      }
    }
  },
