    "displayName": "Event to listen for",
    "name": "firesOn",
    "type": "options",
    "default": "INSERT",
    "options": [
      {
        "name": "Insert",
        "value": "INSERT"
      },
      {
        "name": "Update",
        "value": "UPDATE"
      },
      {
        "name": "Delete",
        "value": "DELETE"
      }
    ],
    "displayOptions": {
      "show": {
        "triggerMode": [
          "createTrigger"
        ]
      }
    }
  },
  {
    "displayName": "Additional Fields",
    "name": "additionalFields",
    "type": "collection",
    "default": {},
    "options": [
      {
        "displayName": "Channel Name",
        "name": "channelName",
        "type": "string",
        "placeholder": "e.g. n8n_channel",
        "description": "Name of the channel to listen to",
        "default": ""
      },
      {
        "displayName": "Function Name",
        "name": "functionName",
        "type": "string",
        "description": "Name of the function to create",
        "placeholder": "e.g. n8n_trigger_function()",
        "default": ""
      },
      {
        "displayName": "Replace if Exists",
        "name": "replaceIfExists",
        "type": "boolean",
        "description": "Whether to replace an existing function and trigger with the same name",
        "default": false
      },
      {
        "displayName": "Trigger Name",
        "name": "triggerName",
        "type": "string",
        "description": "Name of the trigger to create",
        "placeholder": "e.g. n8n_trigger",
        "default": ""
      }
    ],
    "displayOptions": {
      "show": {
        "triggerMode": [
          "createTrigger"
        ]
      }
    }
  },
  {
    "displayName": "Options",
    "name": "options",
    "type": "collection",
    "default": {},
    "options": [
      {
        "displayName": "Connection Timeout",
