[
  {
    "displayName": "Authentication",
    "name": "authentication",
    "type": "options",
    "default": "accessToken",
    "options": [
      {
        "name": "Access Token",
        "value": "accessToken"
      },
      {
        "name": "OAuth2",
        "value": "oAuth2"
      }
    ]
  },
  {
    "displayName": "Resource",
    "name": "resource",
    "type": "string",
    "default": "",
    "description": "The resource ID to subscribe to. The resource can be a task or project.",
    "required": true
  },
  {
    "displayName": "Workspace Name or ID",
    "name": "workspace",
    "type": "options",
    "default": "",
    "description": "The workspace ID the resource is registered under. This is only required if you want to allow overriding existing webhooks. Choose from the list, or specify an ID using an <a href=\"https://docs.n8n.io/code/expressions/\">expression</a>.",
    "options": [],
    "typeOptions": {
      "loadOptionsMethod": "getWorkspaces"
    }
  }
]

[
  {
    "displayName": "Event",
    "name": "event",
    "type": "options",
    "default": "",
    "options": [
      {
        "name": "Contact Added",
        "value": "contactAdded"
      },
      {
        "name": "Contact Added To List",
        "value": "contactAddedToList"
      },
      {
        "name": "Contact Entered Segment",
        "value": "contactEnteredSegment"
      },
      {
        "name": "Contact Left Segment",
        "value": "contactLeftSegment"
      },
      {
        "name": "Contact Removed From List",
        "value": "contactRemovedFromList"
      },
      {
        "name": "Contact Unsubscribed",
        "value": "contactUnsubscribed"
      },
      {
        "name": "Contact Updated",
        "value": "contactUpdated"
      }
    ],
    "required": true
  }
]

[
  {
    "displayName": "Topic",
    "name": "topic",
    "type": "resourceLocator",
    "default": {
      "mode": "list",
      "value": ""
    },
    "required": true
  }
]

[
  {
    "displayName": "Resource",
    "name": "resource",
    "type": "options",
    "default": "workspace",
    "options": [
      {
        "name": "Repository",
        "value": "repository"
      },
      {
        "name": "Workspace",
        "value": "workspace"
      }
    ],
    "required": true,
    "noDataExpression": true
  },
  {
    "displayName": "Workspace Name or ID",
    "name": "workspace",
    "type": "options",
    "default": "",
    "description": "The repository of which to listen to the events. Choose from the list, or specify an ID using an <a href=\"https://docs.n8n.io/code/expressions/\">expression</a>.",
    "required": true,
    "displayOptions": {
      "show": {
        "resource": [
          "workspace",
          "repository"
        ]
      }
    },
    "typeOptions": {
      "loadOptionsMethod": "getWorkspaces"
    }
  },
  {
    "displayName": "Event Names or IDs",
    "name": "events",
    "type": "multiOptions",
    "default": [],
    "description": "The events to listen to. Choose from the list, or specify IDs using an <a href=\"https://docs.n8n.io/code/expressions/\">expression</a>.",
    "options": [],
    "required": true,
    "displayOptions": {
      "show": {
        "resource": [
          "workspace"
        ]
      }
    },
    "typeOptions": {
      "loadOptionsMethod": "getWorkspaceEvents"
    }
  },
  {
    "displayName": "Repository Name or ID",
    "name": "repository",
    "type": "options",
    "default": "",
    "description": "The repository of which to listen to the events. Choose from the list, or specify an ID using an <a href=\"https://docs.n8n.io/code/expressions/\">expression</a>.",
    "required": true,
    "displayOptions": {
      "show": {
        "resource": [
          "repository"
        ]
      }
    },
    "typeOptions": {
      "loadOptionsMethod": "getRepositories",
      "loadOptionsDependsOn": [
        "workspace"
      ]
    }
  },
  {
    "displayName": "Event Names or IDs",
    "name": "events",
    "type": "multiOptions",
    "default": [],
    "description": "The events to listen to. Choose from the list, or specify IDs using an <a href=\"https://docs.n8n.io/code/expressions/\">expression</a>.",
    "options": [],
    "required": true,
    "displayOptions": {
      "show": {
        "resource": [
          "repository"
        ]
      }
    },
    "typeOptions": {
      "loadOptionsMethod": "getRepositoriesEvents"
    }
  }
]

